mapper.select:
select

from ems_course_info


        course_name = #{courseName}


        and course_attr = #{courseAttr}

select id, semester, (select major_name from ems_major where ems_major.id = majorNo) as majorName, (select name from ems_college where ems_college.id = collegeNo) as collegeName, (select name from ems_classes where ems_classes.id = classNo) as className, (select course_name from ems_course_info where ems_course_info.course_id = courseNo) as courseName, (select username from ems_user where number = teacherNo) as teacherName, courseAttr, studentNumber, weeksSum, weeksNumber, isFix, classTime from ems_class_task
and teacherNo = (select number from ems_user where username = #{teacherName})
and classNo = (select id from ems_classes where name = #{className})
and courseNo = (select course_id from ems_course_info where course_name = #{courseName})
and courseAttr = #{courseAttr}
and semester = #{semester}

mapper.update:
update ems_course_info
set course_id   = #{courseId,jdbcType=INTEGER},
    course_name = #{courseName,jdbcType=VARCHAR},
    course_attr = #{courseAttr,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}
