mapper.select:
select id,course_id,course_name,class_time,course_attr,type,totalNum,remaining,semester,
(select username from ems_user where number = teacher_id) as teacherName,
(select CONCAT(etb.build_name,r.classroomName) from ems_classroom r left join ems_teach_build etb on r.teach_build_id = etb.id where r.id = classroom_id) as className
from ems_electivecourse


  course_name = #{courseName}


  and type = #{type}


  and semester = #{semester}

mapper.update:
update ems_electivecourse set course_id = #{courseId,jdbcType=VARCHAR}, course_name = #{courseName,jdbcType=VARCHAR}, teacher_id = #{teacherId,jdbcType=VARCHAR}, classroom_id = #{classroomId,jdbcType=VARCHAR}, class_time = #{classTime,jdbcType=VARCHAR}, course_attr = #{courseAttr,jdbcType=VARCHAR}, type = #{type,jdbcType=INTEGER},
remaining = #{remaining,jdbcType=INTEGER},
semester = #{semester}
where id = #{id,jdbcType=INTEGER}
