mapper.select:
select id, number, username, (select name from ems_classes where ems_classes.id = ems_user.classes) as classes, (select grade from ems_student_grade where ems_student_grade.number = ems_user.number) as grade, (select time from ems_student_grade where ems_student_grade.number = ems_user.number) as time, (select person from ems_student_grade where ems_student_grade.number = ems_user.number) as person, (select type from ems_student_grade where ems_student_grade.number = ems_user.number) as type FROM ems_user
id in(SELECT user_id FROM ems_user_role where role_id = 2)
and number = #{number}
and username = #{username}
and classes = (select id from ems_classes where ems_classes.name = #{classes})
